div.logo{background:url("logo.png") no-repeat}@media(min--moz-device-pixel-ratio:1.3),(-o-min-device-pixel-ratio:2.6 / 2),(-webkit-min-device-pixel-ratio:1.3),(min-device-pixel-ratio:1.3),(min-resolution:1.3dppx){div.logo{background-image:url("logo2x.png");background-size:100px 25px}}.article{*zoom:1}.article:before,.article:after{content:"";display:table}.article:after{clear:both}*,*:after,*:before{-webkit-box-sizing:border-box;-moz-box-sizing:border-box;box-sizing:border-box}.button{-webkit-border-radius:5px;border-radius:5px;background-clip:padding-box}.submit-button{-webkit-border-top-right-radius:10px;border-top-right-radius:10px;-webkit-border-top-left-radius:10px;border-top-left-radius:10px;background-clip:padding-box}.article-heading{opacity:.8;filter:alpha(opacity=80)}.footer-wrap{width:450px;display:block;margin-left:auto;margin-right:auto}.text-truncate{overflow:hidden;text-overflow:ellipsis;white-space:nowrap}.abs{top:10px;right:10px;bottom:5px;left:15px;position:absolute}body{font-size:16px;font-size:2rem}.icon{display:-moz-inline-stack;display:inline-block;vertical-align:top;zoom:1;*display:inline}.header h1{border:0;color:transparent;font:0/0 a;text-shadow:none}body{line-height:16px;line-height:2rem}*{margin:0;padding:0}#ContainerNatal{-webkit-transition:opacity,0.5s,ease,visibility,0.5s,ease;-moz-transition:opacity,0.5s,ease,visibility,0.5s,ease;-ms-transition:opacity,0.5s,ease,visibility,0.5s,ease;transition:opacity,0.5s,ease,visibility,0.5s,ease;overflow:hidden;position:absolute;height:100%;width:100%;z-index:10;top:0;position: fixed;}#ContainerNatal.is-closed{opacity:0;visibility:hidden}#pixie{z-index:0;background:rgba(245,220,54,0.96);background:-moz-linear-gradient(top,rgba(245,220,54,0.96) 0,rgba(237,110,32,0.95) 100%);background:-webkit-gradient(left top,left bottom,color-stop(0,rgba(245,220,54,0.96)),color-stop(100%,rgba(237,110,32,0.95)));background:-webkit-linear-gradient(top,rgba(245,220,54,0.96) 0,rgba(237,110,32,0.95) 100%);background:-o-linear-gradient(top,rgba(245,220,54,0.96) 0,rgba(237,110,32,0.95) 100%);background:-ms-linear-gradient(top,rgba(245,220,54,0.96) 0,rgba(237,110,32,0.95) 100%);background:linear-gradient(to bottom,rgba(245,220,54,0.96) 0,rgba(237,110,32,0.95) 100%);filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#f5dc36',endColorstr='#ed6e20',GradientType=0);height:100%;width:100%}.l,.r{height:50%;position:relative;width:100%}@media only screen and (min-width:992px){.l,.r{display:inline-block;float:left;height:100%;width:50%}}.Arte{height:100%;left:0;margin:0 auto;max-width:90%;position:absolute;right:0;top:0;width:100%;z-index:1}.Arte img{display:block;margin:0 auto}@media only screen and (min-width:992px){.Arte{max-width:970px}}@media only screen and (min-width:1200px){.Arte{max-width:1170px}}#Logo{left:50%;top:50%;bottom:auto;right:auto;-moz-transform:translateX(-50%) translateY(-50%);-o-transform:translateX(-50%) translateY(-50%);-ms-transform:translateX(-50%) translateY(-50%);-webkit-transform:translateX(-50%) translateY(-50%);transform:translateX(-50%) translateY(-50%);max-width:80%;position:absolute;z-index:2}@media only screen and (min-width:992px){#Logo{max-width:100%}}#Calendario{left:50%;top:50%;bottom:auto;right:auto;-moz-transform:translateX(-50%) translateY(-50%);-o-transform:translateX(-50%) translateY(-50%);-ms-transform:translateX(-50%) translateY(-50%);-webkit-transform:translateX(-50%) translateY(-50%);transform:translateX(-50%) translateY(-50%);max-height:80%;position:absolute}@media only screen and (min-width:992px){#Calendario{max-width:100%}}#CloseNatal{display:inline-block;height:24px;position:absolute;right:30px;top:30px;width:32px;z-index:10}#CloseNatal::after,#CloseNatal::before{background-color:#FFF;content:"";display:inline-block;height:3px;left:0;position:absolute;top:10px;width:100%}#CloseNatal::after{-moz-transform:rotate(45deg);-o-transform:rotate(45deg);-ms-transform:rotate(45deg);-webkit-transform:rotate(45deg);transform:rotate(45deg)}#CloseNatal::before{-moz-transform:rotate(-45deg);-o-transform:rotate(-45deg);-ms-transform:rotate(-45deg);-webkit-transform:rotate(-45deg);transform:rotate(-45deg)}.pace{-webkit-pointer-events:none;pointer-events:none;-webkit-user-select:none;-moz-user-select:none;user-select:none}.pace.pace-inactive .pace-progress{display:none}.pace .pace-progress{position:fixed;z-index:2000;top:0;right:0;height:5rem;width:5rem;-webkit-transform:translate3d(0,0,0) !important;-ms-transform:translate3d(0,0,0) !important;transform:translate3d(0,0,0) !important}.pace .pace-progress:after{display:block;position:absolute;top:0;right:.5rem;content:attr(data-progress-text);font-family:"Helvetica Neue",sans-serif;font-weight:100;font-size:5rem;line-height:1;text-align:right;color:rgba(0,0,0,0.2)}
</style>


<div id="ContainerNatal">
  <canvas id="pixie"></canvas>
  <a href="#/" id="CloseNatal"></a>

  <div class="Arte">
  	<div class="l">
  		<img src="/natal/assets/img/kv.png" alt="" id="Logo">
  	</div>
  	
  	<div class="r">
  		<img src="/natal/assets/img/calendario.png" alt="" id="Calendario">
  	</div>
  </div>

</div>
	
	<script src="assets/js/jquery.js"></script>

<script>
  $(function(){
    $(document).ready(function(){
      if ( $( "#ContainerNatal" ).length ) {
        $('body').css('overflow', 'hidden');
      }

      var $mountains = $('#mountains');
      var $grass = $('#grass');
      var $container = $('#ContainerNatal');
      $container.mousedown(function(ev){
        var ox = ev.clientX;
        var om = parseInt($mountains.css('background-position').substr(0, $mountains.css('background-position').search(' ')));
        var og = parseInt($grass.css('background-position').substr(0, $grass.css('background-position').search(' ')));
        $container.mousemove(function(e){
          $mountains.css('background-position', om+((e.clientX-ox)/10)+'px 0px');
          $grass.css('background-position', og+((e.clientX-ox)/4)+'px 10px');
        });
        $container.mouseup(function(){
          $container.unbind('mousemove');
          $container.unbind('mouseup');
        });
      });

      $('#CloseNatal').on('click', function(e){
        e.preventDefault();

        $('#ContainerNatal').addClass('is-closed');
        $('body').css('overflow-y', 'auto');
      })

      setTimeout(function(){
        $('#ContainerNatal').addClass('is-closed');
        $('body').css('overflow-y', 'auto');
      }, 10000);
    });
  })
</script>

<script>
  var WIDTH = window.innerWidth,
    HEIGHT = window.innerHeight,
    MAX_PARTICLES = 400,
    DRAW_INTERVAL = 30,
    container = document.querySelector('#ContainerNatal'),
    canvas = document.querySelector('#pixie'),
    context = canvas.getContext('2d'),
    gradient = null,
    pixies = new Array();

  function setDimensions(e) {
    WIDTH = window.innerWidth;
    HEIGHT = window.innerHeight;
    container.style.width = WIDTH+'px';
    container.style.height = HEIGHT+'px';
    canvas.width = WIDTH;
    canvas.height = HEIGHT;
  }
  setDimensions();
  window.addEventListener('resize', setDimensions);

  function Circle() {
    this.settings = {ttl:8000, xmax:5, ymax:2, rmax:10, rt:1, xdef:960, ydef:540, xdrift:4, ydrift: 4, random:true, blink:true};

    this.reset = function() {
      this.x = (this.settings.random ? WIDTH*Math.random() : this.settings.xdef);
      this.y = (this.settings.random ? HEIGHT*Math.random() : this.settings.ydef);
      this.r = ((this.settings.rmax-1)*Math.random()) + 1;
      this.dx = (Math.random()*this.settings.xmax) * (Math.random() < .5 ? -1 : 1);
      this.dy = (Math.random()*this.settings.ymax) * (Math.random() < .5 ? -1 : 1);
      this.hl = (this.settings.ttl/DRAW_INTERVAL)*(this.r/this.settings.rmax);
      this.rt = Math.random()*this.hl;
      this.settings.rt = Math.random()+1;
      this.stop = Math.random()*.2+.4;
      this.settings.xdrift *= Math.random() * (Math.random() < .5 ? -1 : 1);
      this.settings.ydrift *= Math.random() * (Math.random() < .5 ? -1 : 1);
    }

    this.fade = function() {
      this.rt += this.settings.rt;
    }

    this.draw = function() {
      if(this.settings.blink && (this.rt <= 0 || this.rt >= this.hl)) {
        this.settings.rt = this.settings.rt*-1;
      } else if(this.rt >= this.hl) {
        this.reset();
      }

      var newo = 1-(this.rt/this.hl);
      context.beginPath();
      context.arc(this.x, this.y, this.r, 0, Math.PI*2, true);
      context.closePath();

      var cr = this.r*newo;
      gradient = context.createRadialGradient(this.x, this.y, 0, this.x, this.y, (cr <= 0 ? 1 : cr));
      gradient.addColorStop(0.0, 'rgba(255,255,255,'+newo+')');
      gradient.addColorStop(this.stop, 'rgba(234,220,20,'+(newo*.6)+')');
      gradient.addColorStop(1.0, 'rgba(234,220,20,0)');
      context.fillStyle = gradient;
      context.fill();
    }

    this.move = function() {
      this.x += (this.rt/this.hl)*this.dx;
      this.y += (this.rt/this.hl)*this.dy;
      if(this.x > WIDTH || this.x < 0) this.dx *= -1;
      if(this.y > HEIGHT || this.y < 0) this.dy *= -1;
    }

    this.getX = function() { return this.x; }
    this.getY = function() { return this.y; }
  }

  for (var i = 0; i < MAX_PARTICLES; i++) {
    pixies.push(new Circle());
    pixies[i].reset();
  }

  function draw() {
    context.clearRect(0, 0, WIDTH, HEIGHT);
    for(var i = 0; i < pixies.length; i++) {
      pixies[i].fade();
      pixies[i].move();
      pixies[i].draw();
    }
  }
  setInterval(draw, DRAW_INTERVAL);
</script>
</body>
</html>